import React, { useState, useEffect } from 'react';
import CircularProgress from '@material-ui/core/CircularProgress';
import DatePicker from 'react-datepicker';
import axios from 'axios';

const WeatherDisplay = ({zip}) => {
    const initData = {
        weatherData:{},
        error: '',
        isLoading: true
    }

    const [weatherData, setWeatherData] = useState(initData.weatherData);
    const [error, setError] = useState(initData.error);
    const [isLoading, setIsLoading] = useState(initData.isLoading);
    const [startDate, setStartDate] = useState(new Date());

    const URL = "/api/location/"+zip+"/";

    useEffect(() => {
        axios(URL, {
            method: 'GET',
          }).then(response => {
              console.log(response)
              setWeatherData(response);
              setIsLoading(false);
          }).catch((err)=>{
              console.log(err)
              setError(err)
            })

        /*fetch(URL, {
            'headers':{
                'Content-Type':'application/json',
                'Access-Control-Allow-Origin': '*',
                'Access-Control-Allow-Origin': 'https://www.metaweather.com',
                'Access-Control-Allow-Credentials': true,
                'Access-Control-Expose-Headers': '*',
                'Access-Control-Allow-Methods': 'POST, GET, PUT, DELETE, OPTIONS'
            }}).then(res => res.json()).then(json => {
            //setWeatherData(json.consolidated_weather);
            //setIsLoading(false);
            console.log(JSON.stringify(json))
          }).catch(err => {
            setError(err)
          })*/
    }, []);

    useEffect(() => { 
        console.log('update')
    });

    useEffect(() => {
        return () => {
          console.log('will unmount');
        }
      }, []);

    return (
        <div>
    <h1>Displaying some Weather! {zip}</h1>
    <div>{ isLoading ? <CircularProgress /> : <div><h1>
      {weatherData.title}
    </h1>
    {weatherData.consolidated_weather.map(item => (
        <span><p>Current: {item.the_temp}°</p>
        <p>High: {item.max_temp}°</p>
        <p>Low: {item.min_temp}°</p>
        <p>Wind Speed: {item.wind_speed} mi/hr</p></span>
    ))}

    <DatePicker selected={startDate} onChange={date => setStartDate(date)} />
    </div>

    }</div>
    </div>
      );
}

export  { WeatherDisplay };
