import "bootstrap/dist/css/bootstrap.css";
import React, { useState, useEffect } from 'react';
import { Navbar, NavItem, Nav, Row, Col } from "react-bootstrap";
import Geolocation from 'react-native-geolocation-service';
import { Grid } from '@material-ui/core';
import  styles from './App.module.css';
import { WeatherDisplay } from './WeatherDisplay/WeatherDisplay';

const PLACES = [
  { name: "Palo Alto", zip: "94303", id: 1 },
  { name: "San Jose", zip: "94088", id: 2 },
  { name: "Santa Cruz", zip: "95062", id: 3 },
  { name: "Honolulu", zip: "96803", id: 4 }
];

const App = () => {
  const initData = {
    activePlace: 0,
    weatherData: null
  };

  const [activePlace , setActivePlace] = useState(initData.activePlace);

  useEffect(() => { 
    console.log('update')
  });

  useEffect(() => { 
    console.log('did mount')
  }, []);


useEffect(() => {
    return () => {
      console.log('will unmount');
    }
  }, []);


  const onCityClickHandler = (id) => {
    setActivePlace(id);
    console.log(activePlace);
  };

  return (
    <div>
  <Navbar>
    <Navbar.Header>
      <Navbar.Brand>
        React Simple Weather App
      </Navbar.Brand>
    </Navbar.Header>
  </Navbar>
  <Grid>
    <Row>
      <Col md={4} sm={4}>
        <h3>Select a city</h3>
        <Nav
          bsStyle="pills"
          stacked
          activeKey={activePlace}
          onSelect={index => {
            onCityClickHandler(index);
          }}
        >
          {PLACES.map((place, index) => (
            <NavItem key={index} eventKey={index}>{place.name}</NavItem>
          ))}
        </Nav>
      </Col>
      <Col md={8} sm={8}>
        <WeatherDisplay key={activePlace} zip={PLACES[activePlace].zip} />
      </Col>
    </Row>
  </Grid>
</div>
  );
