import React, { useState, useEffect } from 'react';
import CircularProgress from '@material-ui/core/CircularProgress';
import DatePicker from 'react-datepicker';
import axios from 'axios';
import  styles from '../App.module.css';

import 'react-datepicker/dist/react-datepicker.css';


import Grid from '@material-ui/core/Grid';
import FormLabel from '@material-ui/core/FormLabel';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import RadioGroup from '@material-ui/core/RadioGroup';
import Radio from '@material-ui/core/Radio';
import Paper from '@material-ui/core/Paper';


const WeatherDisplay = ({zip}) => {
    const initData = {
        weatherData:{},
        error: '',
        isLoading: true
    }

    const [weatherData, setWeatherData] = useState(initData.weatherData);
    const [error, setError] = useState(initData.error);
    const [isLoading, setIsLoading] = useState(initData.isLoading);
    const [startDate, setStartDate] = useState(new Date());

    const URL = "/api/location/"+zip+"/";

    const onClickDateHandler = (date) => {
        alert(date)
        setStartDate(date);
        const year = date.getFullYear();
        const month = date.getMonth() + 1;
        const day = date.getDate();

        axios(URL+year+"/"+month+"/"+day+"/", {
            method: 'GET',
          }).then(response => {
              console.log(response.data)
              setWeatherData({consolidated_weather: response.data});
              setIsLoading(false);
          }).catch((err)=>{
              console.log(err)
              setError(err)
            });

    }

    useEffect(() => {
        axios(URL, {
            method: 'GET',
          }).then(response => {
              console.log(response.data)
              setWeatherData(response.data);
              setIsLoading(false);
          }).catch((err)=>{
              console.log(err)
              setError(err)
            });

        /*fetch(URL, {
            'headers':{
                'Content-Type':'application/json',
                'Access-Control-Allow-Origin': '*',
                'Access-Control-Allow-Origin': 'https://www.metaweather.com',
                'Access-Control-Allow-Credentials': true,
                'Access-Control-Expose-Headers': '*',
                'Access-Control-Allow-Methods': 'POST, GET, PUT, DELETE, OPTIONS'
            }}).then(res => res.json()).then(json => {
            //setWeatherData(json.consolidated_weather);
            //setIsLoading(false);
            console.log(JSON.stringify(json))
          }).catch(err => {
            setError(err)
          })*/
    }, []);

    useEffect(() => { 
        console.log('update')
    });

    useEffect(() => {
        return () => {
          console.log('will unmount');
        }
      }, []);

    return (
        <div>
    <h1>Displaying some Weather! {zip}</h1>
    <div>{ isLoading ? <CircularProgress /> : <Grid item lg={12}><h1>
      {weatherData.title}
      <DatePicker selected={startDate} onChange={date => onClickDateHandler(date)} />
    </h1>
    <Grid container justify="center" direction="row" spacing={2}>
    { weatherData.consolidated_weather.map(item => (
        <Grid key={item.id} item>
            <p>{item.created}</p>
            <img src={"https://www.metaweather.com/static/img/weather/png/64/"+item.weather_state_abbr+".png"} alt={item.weather_state_name} />
            <p>High: {item.max_temp.toFixed()}°</p>
            <p>Low: {item.min_temp.toFixed()}°</p>
            <p>Wind Speed: {item.wind_speed.toFixed()} mi/hr</p>
        </Grid>
        ))
    }
    </Grid>
    </Grid>

    }</div>
    </div>
    );
