import React, { useState, useEffect } from 'react';
import CircularProgress from '@material-ui/core/CircularProgress';
import DatePicker from 'react-datepicker';

const WeatherDisplay = ({zip}) => {
    const initData = {
        weatherData: {},
        error: '',
        isLoading: true
    }

    const [weatherData, setWeatherData] = useState(initData.weatherData);
    const [error, setError] = useState(initData.error);
    const [isLoading, setIsLoading] = useState(initData.isLoading);
    const [startDate, setStartDate] = useState(new Date());

    const URL = "https://www.metaweather.com/api/location/"+zip+"/";

    useEffect(() => {
        fetch(URL).then(res => res.json()).then(json => {
            setWeatherData(json);
            setIsLoading(false);
          }).catch(err => {
            setError(err)
          })
    }, []);

    useEffect(() => { 
        console.log('update')
    });

    useEffect(() => {
        return () => {
          console.log('will unmount');
        }
      }, []);

    return (
        <div>
    <h1>Displaying some Weather! {zip}</h1>
    <div>{ isLoading ? <CircularProgress /> : <div><h1>
      {weatherData.name}
      <img src={"http://openweathermap.org/img/w/" + weatherData.weather[0].icon + ".png"} alt={weatherData.description} />
    </h1>
    <p>Current: {weatherData.main.temp}°</p>
    <p>High: {weatherData.main.temp_max}°</p>
    <p>Low: {weatherData.main.temp_min}°</p>
    <p>Wind Speed: {weatherData.wind.speed} mi/hr</p>
    <DatePicker selected={startDate} onChange={date => setStartDate(date)} />
    </div>

    }</div>
    </div>
      );
}

export  { WeatherDisplay };
