import React, { useState, useEffect } from 'react';
import Container from '@material-ui/core/Container';


import { WeatherDisplay } from './WeatherDisplay/WeatherDisplay';

const PLACES = [
  { name: "Moscow", zip: "2122265", id: 1 },
  { name: "San Jose", zip: "94088", id: 2 },
  { name: "Santa Cruz", zip: "95062", id: 3 },
  { name: "Honolulu", zip: "96803", id: 4 }
];

const App = () => {
  const initData = {
    activePlace: 0,
    weatherData: null
  };

  const [activePlace , setActivePlace] = useState(initData.activePlace);

  useEffect(() => { 
    console.log('update')
  });

  useEffect(() => { 
    console.log('did mount')
  }, []);


useEffect(() => {
    return () => {
      console.log('will unmount');
    }
  }, []);


  const onCityClickHandler = (id) => {
    setActivePlace(id);
    console.log(activePlace);
  };


  return (
    <Container >
      <WeatherDisplay  key={activePlace}
          zip={PLACES[activePlace].zip} />
      {PLACES.map((place,index) => (
      <button
        key={index}
        onClick={() => onCityClickHandler(index)}
      >
          {place.name}
      </button>
    ))}
  </Container>

);
};

export default App;
