import React, { useState, useEffect } from 'react';
import CircularProgress from '@material-ui/core/CircularProgress';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import axios from 'axios';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import IconButton from '@material-ui/core/IconButton';
import HomeIcon from '@material-ui/icons/Home';
import PropTypes from 'prop-types';

import  styles from '../App.module.css';

const WeatherDisplay = ({zip}) => {
    const initData = {
        weatherData:{},
        error: '',
        isLoading: true,
        hasHistory: false,
    };

    const [weatherData, setWeatherData] = useState(initData.weatherData);
    const [error, setError] = useState(initData.error);
    const [isLoading, setIsLoading] = useState(initData.isLoading);
    const [startDate, setStartDate] = useState(new Date());
    const [hasHistory, setHistory] = useState(initData.hasHistory);

    const URL = "/api/location/"+zip+"/";

    const onClickDateHandler = (date) => {
        setStartDate(date);
        const year = date.getFullYear();
        const month = date.getMonth() + 1;
        const day = date.getDate();

        axios(URL+year+"/"+month+"/"+day+"/", {
            method: 'GET',
          }).then(response => {
              setWeatherData({ consolidated_weather: response.data });
              setIsLoading(false);
              setHistory(true);
          }).catch( ( err ) => {
              setError(err);
            });

    };

    useEffect( () => {
        axios(URL, {
            method: 'GET',
          }).then(response => {
              setWeatherData(response.data);
              setIsLoading(false);
          }).catch( ( err ) => {
              setError(err);
            });
    }, []);

    useEffect(() => { 
        console.log('update');
    });

    useEffect( () => {
        return () => {
          console.log('will unmount');
        };
      }, []);

    return (
        <div>
            <h1>Show weather!  { hasHistory ? <span>
                    <IconButton 
                    aria-label="home page" 
                    onClick={()=> window.location.reload()}>
                        <HomeIcon />
                    </IconButton>
                    </span>: ''}</h1>
            <div>{ isLoading ? <CircularProgress /> : 
                    !isLoading && error ? <span>{error}</span> : 
                        <Grid item lg={12}>
                            <h1>{weatherData.title ? weatherData.title : 'History'}<br/>
                                <DatePicker selected={startDate} onChange={date => onClickDateHandler(date)} />
                            </h1>
                            { !hasHistory ?
                             <Grid container justify="center" direction="row" spacing={2}>
                                 { weatherData.consolidated_weather.map(item => (
                                    <Grid key={item.id} item>
                                        <p>{item.applicable_date }</p>
                                        <img src={"https://www.metaweather.com/static/img/weather/png/64/"+item.weather_state_abbr+".png"} alt={item.weather_state_name} />
                                        <p>High: {item.max_temp.toFixed()}째</p>
                                        <p>Low: {item.min_temp.toFixed()}째</p>
                                        <p>Wind Speed: {item.wind_speed.toFixed()} mi/hr</p>
                                    </Grid>
                                    ))}
                            </Grid> : 
                            <Grid container direction="row">
                                { weatherData.consolidated_weather.map(item =>
                                    <Grid key={item.id} item>
                                        <img src={"https://www.metaweather.com/static/img/weather/png/64/"+item.weather_state_abbr+".png"} alt={item.weather_state_name} />
                                        <p>{item.max_temp.toFixed()}째</p>
                                        <p>{item.min_temp.toFixed()}째</p>
                                    </Grid>

                                )}
                            </Grid>}
                        </Grid>}
            </div>
        </div>
    );
}

WeatherDisplay.propTypes = {

    zip: PropTypes.oneOfType([
        PropTypes.string,
        PropTypes.number
      ])
};

export  { WeatherDisplay };
