import React, { useState, useEffect } from 'react';
import CircularProgress from '@material-ui/core/CircularProgress';
import DatePicker from 'react-datepicker';
import axios from 'axios';

const WeatherDisplay = ({zip}) => {
    const initData = {
        weatherData: [],
        error: '',
        isLoading: true
    }

    const [weatherData, setWeatherData] = useState(initData.weatherData);
    const [error, setError] = useState(initData.error);
    const [isLoading, setIsLoading] = useState(initData.isLoading);
    const [startDate, setStartDate] = useState(new Date());

    const URL = "https://www.metaweather.com/api/location/"+zip+"/";

    useEffect(() => {
        axios.get(URL, {headers: {'Access-Control-Allow-Origin': '*', 'Access-Control-Allow-Credentials': true, 'Access-Control-Allow-Methods': 'POST, GET, PUT, DELETE, OPTIONS' }}).then(json => console.log(json)).catch(err => {
            setError(err)
            console.log("Err="+err)
          })
        /*fetch(URL, {
            'headers':{
                'Content-Type':'application/json',
                'Access-Control-Allow-Origin': '*',
                'Access-Control-Allow-Origin': 'https://www.metaweather.com',
                'Access-Control-Allow-Credentials': true,
                'Access-Control-Expose-Headers': '*',
                'Access-Control-Allow-Methods': 'POST, GET, PUT, DELETE, OPTIONS'
            }}).then(res => res.json()).then(json => {
            //setWeatherData(json.consolidated_weather);
            //setIsLoading(false);
            console.log(JSON.stringify(json))
          }).catch(err => {
            setError(err)
          })*/
    }, []);

    useEffect(() => { 
        console.log('update')
    });

    useEffect(() => {
        return () => {
          console.log('will unmount');
        }
      }, []);

    return (
        <div>
    <h1>Displaying some Weather! {zip}</h1>
    <div>{ isLoading ? <CircularProgress /> : <div><h1>
      {weatherData.name}
    </h1>
    <p>Current: {weatherData.main.temp}°</p>
    <p>High: {weatherData.main.temp_max}°</p>
    <p>Low: {weatherData.main.temp_min}°</p>
    <p>Wind Speed: {weatherData.wind.speed} mi/hr</p>
    <DatePicker selected={startDate} onChange={date => setStartDate(date)} />
    </div>

    }</div>
    </div>
      );
}

export  { WeatherDisplay };
